import React, { Component } from 'react';
//import dbConnections from './db.Connections';
import './App.css';

class App extends Component {
  state = {
    datos: []
  }
  componentDidMount(){
    this.interval = setInterval(() => this.getData(), 1000);
  }
  getData = _ => {
    fetch('http://localhost:4000')
    .then(response => response.json())
    .then(response => this.setState({datos: response.data}))
    .catch(err => console.error(err))
  }
  render() {
    const todos = this.state.datos.map((dato, i)=>{
      return (
        <tr>
          <th><div>{dato.ID}</div></th>
          <th><div>{dato.Fecha}</div></th>
          <th><div>{dato.Latitud}</div></th>
          <th><div>{dato.Longitud}</div></th>
        </tr>
      )
    })
    return (
      <div className="App">
        <nav className="navbar navbar-dark bg-dark" id="navbar">
          <a href="/" className="text-white">
            Datos Ubicación
          </a>
        </nav>
        <table class="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Fecha</th>
              <th>Latitud</th>
              <th>Longitud</th>
            </tr>
          </thead>
          <tbody>
            {todos}                            
          </tbody> 
        </table>
      </div>
    );
  }
}

export default App;
