import React, { Component } from 'react';
import './App.css';
//import {refresh} from './datos.js' ;

var refresh = function () {
  var con = dbConnections();
  con.query('SELECT * FROM syrus order by ID desc limit 1', [], function (err, result) {
      var datos = result;       
  })
}
setInterval(refresh,1000);

class App extends Component {
  constructor(){
    super();
    this.state = {
      datos
    }
  }
  render() {
    const todos = this.state.datos.map((dato, i)=>{
      return (
        <tr>
          <th><div>{dato.ID}</div></th>
          <th><div>{dato.Fecha}</div></th>
          <th><div>{dato.Latitud}</div></th>
          <th><div>{dato.Longitud}</div></th>
        </tr>
      )
    })
    return (
      <div className="App">
        <nav className="navbar navbar-dark bg-dark" id="navbar">
          <a href="/" className="text-white">
            Datos Ubicación
          </a>
        </nav>
        <table class="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Fecha</th>
              <th>Latitud</th>
              <th>Longitud</th>
            </tr>
          </thead>
          <tbody>
            {todos}                            
          </tbody> 
        </table>
      </div>
    );
  }
}

export default App;
